val FONT_SIZE = intPreferencesKey("font_size")
val SHOW_BOTTOM_BAR = booleanPreferencesKey("show_bottom_bar")
const val INITIAL_FONT_SIZE = 16
const val INITIAL_SHOW_BOTTOM_BAR = true

class MainActivity : ComponentActivity() {

    companion object {
        private val Context.dataStore: DataStore<Preferences> by preferencesDataStore(name = "settings")
    }

    @OptIn(ExperimentalMaterial3Api::class)
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val fontSizeFlow = dataStore.data.map { preferences ->
            preferences[FONT_SIZE] ?: INITIAL_FONT_SIZE
        }
        val showBottomBarFlow = dataStore.data.map { preferences ->
            preferences[SHOW_BOTTOM_BAR] ?: INITIAL_SHOW_BOTTOM_BAR
        }

        setContent {
            AndroidView(
                modifier = Modifier
                    .fillMaxSize()
                    .verticalScroll(rememberScrollState()),
                factory = { context -> TextView(context) },
                update = { it.text = HtmlCompat.fromHtml(
                    "<div id=\"contents\" bis_skin_checked=\"1\"><h2 class=\"c16\" dir=\"rtl\" id=\"cpe23\"><span class=\"c12\">פרק 23</span></h2><h1 class=\"c17\" dir=\"rtl\" id=\"h.2et92p0\"><span class=\"c11\">אמונה באמונה</span></h1><p class=\"c6\" dir=\"rtl\"><span class=\"c2\">\"וג'נט הייתה סקיבית,\" אמר הדיוקן של האישה הצעירה, שחבשה כובע מעוטר בזהב.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו רשם זאת. היו לו רק עשרים ושמונה, אבל הגיע הזמן לחזור ולפגוש את הארי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא נאלץ להיעזר בדיוקנאות אחרים לתרגום — השפה האנגלית השתנתה משמעותית במהלך השנים — אבל הדיוקנאות העתיקים ביותר תיארו לחשי שנה ראשונה שנשמעו דומים למדי לאלו שהיו להם עכשיו. דראקו זיהה חצי מהם והשאר לא נשמעו חזקים יותר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">תחושת הבחילה בבטנו הלכה וגברה עם כל תשובה עד שלבסוף, כשלא היה מסוגל לעמוד בזה יותר, הוא הפסיק, והלך ושאל דיוקנאות אחרים את השאלה המוזרה של הארי פוטר על נישואי סקיבים. חמשת הדיוקנאות הראשונים לא הכירו אף זוג כזה, ובסופו של דבר הוא ביקש מהדיוקנאות לבקש מהמכרים </span><span class=\"c1\">שלהם</span><span class=\"c2\">&nbsp;לשאול את המכרים </span><span class=\"c1\">שלהם,</span><span class=\"c2\">&nbsp;וכך הצליח למצוא כמה דיוקנאות שהיו מוכנים להודות שהיו להם חברים סקיבים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">(הסְלִיתְ'רִין הצעיר הסביר שהוא עובד על פרויקט חשוב עם תלמיד רֵייבֶנְקְלוֹ, והרֵייבֶנְקְלוֹ אמר לו שהם צריכים את המידע ואז ברח בלי להגיד לו למה. ההסבר זיכה אותו במבטי הזדהות רבים.)</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו הלך ברגליים כבדות במסדרונות הוֹגווֹרטְס. הוא היה צריך לרוץ, אבל לא נראה שהוא מסוגל לאזור את הכוחות. הוא המשיך לחשוב על כך שהוא לא רצה לדעת מזה, לא רצה להיות מעורב בזה, הוא לא רצה שזו תהיה האחריות שלו, שהארי פוטר יתעסק בזה, אם הקסם דועך שהארי יטפל בזה...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל דראקו ידע שזה לא בסדר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">קרים הם צינוקי סְלִיתְ'רִין, אפורים קירות האבן. לרוב, דראקו אהב את האווירה, אבל עכשיו היא הרגישה יותר מדי כמו... דעיכה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ידו הגיעה לידית הדלת. הארי פוטר כבר המתין בפנים, לבוש בגלימה וברדס.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"לחשי השנה הראשונה העתיקים,\" אמר הארי פוטר. \"מה גילית?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"הם לא חזקים יותר מהלחשים שאנחנו משתמשים בהם כיום.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אגרופו של הארי פוטר הוטח בשולחן. \"לעזאזל. בסדר. הניסוי שלי היה כישלון, דראקו. יש משהו שנקרא האיסור של מרלין —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו הכה במצחו, בהבנה פתאומית.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"— שמונע מאנשים ללמוד לחשים רבי עוצמה מתוך ספרים. אפילו אם היית מוצא את ספרי הלחשים של קוסם רב עוצמה, לא היית מצליח להבין אותם, הידע חייב לעבור מתודעה חיה אחת לאחרת. לא הצלחתי למצוא לחשים חזקים שיש לנו את הוראות ההטלה שלהם אבל אנחנו לא יכולים להטיל. אבל אם אתה לא יכול ללמוד אותם מספרים, למה שמישהו יטרח להעביר אותם הלאה מפה לאוזן אחרי שהפסיקו לעבוד? האם השגת את הנתונים על זוגות הסקיבים?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו החל להושיט את הקלף —</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל הארי פוטר הרים את ידו. \"חוק מדעי, דראקו. ראשית אני אגיד לך מה התאוריה ומה היא חוזה, ורק אז אתה תראה לי את הנתונים. כך תדע שאני לא ממציא תאוריה שתתאים; אתה תדע שהתאוריה חזתה את הנתונים </span><span class=\"c1\">מראש</span><span class=\"c2\">. אני צריך להסביר לך את זה בכל מקרה, אז אני אסביר </span><span class=\"c1\">לפני</span><span class=\"c2\">&nbsp;שתראה לי את הנתונים. זה הכלל. אז לבש את הגלימה שלך ובוא נשב.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי פוטר התיישב ליד שולחן עם קרעי נייר מסודרים עליו. דראקו הוציא את גלימתו מתוך התיק, לבש אותה, והתיישב בצד השני מול הארי, מעיף מבט מבולבל בניירות. הם היו מסודרים בשתי שורות ואורך כל שורה היה כעשרים קרעים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"סוד הדם,\" אמר הארי פוטר, מבט מרוכז על פניו, \"הוא משהו שנקרא חומצה דאוקסיריבונוקלאית. אל לך לומר שם זה בפני מי שאיננו מדען. חומצה דאוקסיריבונוקלאית היא המתכון שאומר לגוף שלך איך לגדול — שתי רגליים, שתי ידיים, גבוה או נמוך, האם יש לך עיניים חומות או ירוקות. זה דבר מוחשי, אתה יכול </span><span class=\"c1\">לראות</span><span class=\"c2\">&nbsp;אותו אם יש לך מיקרוסקופ, שהוא כמו טלסקופ, אבל משמש כדי להסתכל על דברים ממש קטנים במקום על דברים ממש רחוקים. ובמתכון הזה יש תמיד שני עותקים מכל דבר, למקרה שאחד העותקים פגום. דמיין שתי שורות ארוכות של חתיכות נייר. בכל מקום בשורה, ישנן שתי פיסות נייר, וכשיש לך ילדים, הגוף שלך בוחר פיסת נייר באקראי מכל מקום בשורה, והגוף של האם עושה את אותו הדבר, וכך הילד גם מקבל שתי חתיכות נייר בכל מקום בשורה. שני עותקים מכל דבר, אחד מאימך, אחד מאביך, וכשיש לך ילדים הם מקבלים חתיכת נייר אחת ממך, באקראי, בכל מקום בשורה.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">בעוד הארי מדבר, אצבעותיו נעו על פני הניירות, מצביעות על אחד מהזוג כשאמר \"מאימך\" ועל השני כשאמר \"מאביך\". וכשהארי דיבר על לבחור חתיכת נייר באקראי, ידו נשלחה לגלימותיו, הוציאה גוז והטילה אותו; הארי הביט במטבע, והצביע על פיסת הנייר העליונה. כל זאת בלי להפסיק לדבר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"עכשיו, כשזה נוגע למשהו כמו להיות גבוה או נמוך, יש </span><span class=\"c1\">הרבה </span><span class=\"c2\">מקומות במתכון שמשפיעים במידה </span><span class=\"c1\">מועטה</span><span class=\"c2\">. אז אם אבא גבוה מתחתן עם אימא נמוכה, הילד יקבל כמה פיסות נייר שאומרות 'גבוה' וכמה פיסות נייר שאומרות 'נמוך', ובדרך כלל הילד יהיה בגובה שבין שני הגבהים. אבל לא תמיד. הילד יכול לקבל במקרה הרבה ניירות שאומרים 'גבוה' ומעט ניירות שאומרים 'נמוך', ולגדול להיות די גבוה. יכול להיות מקרה שבו יש אבא גבוה עם חמישה ניירות שאומרים 'גבוה', ואימא גבוהה עם חמישה ניירות שאומרים 'גבוה', ובמקרה הילד יקבל את </span><span class=\"c1\">כל עשרת הניירות</span><span class=\"c2\">&nbsp;שאומרים 'גבוה' ויגדל להיות יותר גבוה משניהם. אתה מבין? דם הוא לא נוזל מושלם, הוא לא מתערבב לגמרי. חומצה דאוקסיריבונוקלאית מורכבת מהרבה חתיכות קטנות, כמו כוס של חלוקי נחל במקום כוס מים. זו הסיבה שילד הוא לא תמיד בדיוק באמצע בין שני הוריו.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו הקשיב בפה פעור. איך בשם מרלין המוגלגים הצליחו להבין את כל זה? הם יכלו </span><span class=\"c1\">לראות</span><span class=\"c2\">&nbsp;את המתכון?</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"עכשיו,\" אמר הארי פוטר, \"נניח, כמו עם גובה, שיש הרבה מקומות במתכון שבהם יכולים להיות ניירות שאומרים 'קסם' או 'לא קסם'. אם יש לך מספיק ניירות שאומרים 'קסם' אתה קוסם, ואם יש לך </span><span class=\"c1\">הרבה</span><span class=\"c2\">&nbsp;ניירות אתה קוסם חזק, אם יש לך מעט מדי אתה מוגל, ואם אתה באמצע אתה סקיב. ואז, כששני סקיבים מתחתנים, רוב הזמן הילדים שלהם יהיו סקיבים, אבל מדי פעם יתמזל מזלו של אחד הילדים והוא יקבל את רוב ניירות הקסם של אביו </span><span class=\"c1\">וגם</span><span class=\"c2\">&nbsp;את רוב ניירות הקסם של אימו, ויהיה חזק מספיק כדי להיות קוסם. אבל כנראה לא קוסם חזק במיוחד. אם התחלת עם הרבה קוסמים חזקים והם התחתנו רק אחד עם השני, הם היו נשארים חזקים. אבל אם הם התחילו להתחתן עם בני־מוגלגים שהיו בקושי קסומים, או עם סקיבים... אתה מבין? הדם לא יתערבב בצורה מושלמת, זו תהיה כוס של חלוקי נחל, לא כוס של מים, בגלל שככה דם עובד. עדיין יהיו קוסמים חזקים פה ושם, כשהם יקבלו במזל הרבה ניירות קסם. אבל הם לא יהיו חזקים כמו הקוסמים החזקים ביותר מהעבר.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו הנהן באיטיות. הוא מעולם לא שמע את זה מוסבר כך. היה יופי מפתיע באופן שבו זה התאים באופן מושלם.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אבל</span><span class=\"c2\">,\" אמר הארי. \"זו רק השערה </span><span class=\"c1\">אחת</span><span class=\"c2\">. נניח במקומה שיש רק מקום </span><span class=\"c1\">אחד</span><span class=\"c2\">&nbsp;במתכון שהופך אותך לקוסם. מקום אחד שבו יש פיסת נייר שאומרת 'קסם' או 'לא קסם'. ותמיד יש שני עותקים של הכול. ואז יש רק שלוש אפשרויות. שני העותקים אומרים 'קסם'. עותק אחד אומר 'קסם' והשני אומר 'לא קסם'. או ששני העותקים אומרים 'לא קסם'. קוסמים, סקיבים, ומוגלגים. עם שני עותקים אתה יכול להטיל לחשים, עם עותק אחד אתה יכול להשתמש בשיקויים ובחפצי קסם, ובלי אף עותק אתה מתקשה אפילו להסתכל ישירות על קסם. בני־מוגלגים לא באמת ייוולדו למוגלגים, הם ייוולדו לשני סקיבים, שני הורים בעלי עותק 'קסם' אחד שגדלו בעולם המוגלגים. עכשיו דמיין לעצמך שמכשפה מתחתנת עם סקיב. כל ילד יקבל נייר אחד שאומר 'קסם' מהאם, תמיד, זה לא משנה איזו פיסת נייר נבחרת באקראי, שתיהן אומרות 'קסם'. אבל כמו בהטלת מטבע, בחצי מהפעמים הילד יקבל נייר שאומר 'קסם' מהאב, ובחצי מהפעמים הוא יקבל את הנייר של האב שאומר 'לא קסם'. כשמכשפה מתחתנת עם סקיב, הם לא יולידו הרבה קוסמים חלשים. חצי מהילדים יהיו מכשפות וקוסמים, חזקים בדיוק כמו אימם, וחצי מהילדים יהיו סקיבים. בגלל שאם יש רק מקום </span><span class=\"c1\">אחד</span><span class=\"c2\">&nbsp;במתכון שעושה אותך לקוסם, אז קסם הוא לא כמו כוס של חלוקי נחל שיכולים להתערבב. הוא כמו חלוק קסום אחד — אבן החכמים.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי סידר שלושה זוגות של קרעי נייר זה לצד זה. על זוג אחד הוא כתב 'קסם' ו'קסם'. על הזוג השני הוא כתב 'קסם' על הנייר העליון בלבד. ואת הזוג השלישי הוא השאיר ריק.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"ובמקרה הזה,\" אמר הארי, \"או שיש לך שתי אבנים או שאין לך. או שאתה קוסם או שלא. קוסמים חזקים יהפכו לכאלה בכך שילמדו קשה ויתאמנו יותר. ואם קוסמים הופכים פחות חזקים </span><span class=\"c1\">אינהרנטית</span><span class=\"c2\">, לא בגלל שלחשים אובדים אלא בגלל שאנשים לא יכולים להטיל אותם... אז אולי אנחנו אוכלים את המזון הלא נכון או משהו. אבל אם זה הולך ונהיה גרוע יותר בהדרגה במשך שמונה מאות שנה, אז זה אומר שיכול להיות שהקסם עצמו נמוג מהעולם.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי סידר שני זוגות נוספים של קרעי נייר זה לצד זה, והוציא עט־נוצה. במהרה בכל זוג היה נייר אחד שאמר 'קסם' ואחד ריק.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"וזה מביא אותי לתחזית שלי,\" אמר הארי. \"מה קורה כששני סקיבים מתחתנים. תטיל מטבע פעמיים. הוא יכול לצאת עץ ועץ, עץ ופלי, פלי ועץ, או פלי ופלי. אז רבע מהפעמים תקבל פעמיים עץ, רבע מהפעמים תקבל פעמיים פלי, וחצי מהפעמים תקבל עץ ופלי. אותו דבר אם שני סקיבים מתחתנים. רבע מהילדים יקבלו קסם וקסם, ויהיו קוסמים. רבע מהילדים יקבלו לא־קסם ולא־קסם, ויהיו מוגלגים. החצי הנותר יהיו סקיבים. זו תבנית קלאסית וישנה מאוד. מי שגילה אותה היה גרגור מנדל, ששמו לא יישכח, והיא הייתה הרמז הראשון שהתגלה אי־פעם לאופן שבו המתכון עובד. כל מי שיודע משהו על מדע הדם יזהה את התבנית הזו מיד. זה לא יהיה מדויק, בדיוק כמו שכאשר אתה מטיל מטבע פעמיים ועושה ארבעים חזרות לא תקבל בדיוק עשרה זוגות של פעמיים עץ. אבל אם זה שבעה או שלושה־עשר קוסמים מתוך ארבעים ילדים זה יהיה מחוון חזק. זו הבדיקה שביקשתי שתעשה. עכשיו בוא נראה את הנתונים שלך.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ולפני שדראקו הספיק לחשוב, הארי פוטר לקח את הקלף מידו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">גרונו של דראקו היה יבש מאוד.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">עשרים ושמונה ילדים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא לא היה בטוח במספר המדויק, אבל הוא היה די בטוח שבערך רבע היו קוסמים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"שישה קוסמים מתוך עשרים ושמונה ילדים,\" אמר הארי פוטר לאחר זמן מה. \"טוב, אז זהו זה. ותלמידי השנה הראשונה הטילו את אותם הלחשים באותה עוצמה לפני שמונה מאות שנה. הבדיקה שלי והבדיקה שלך יצאו אותו דבר.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">שתיקה ארוכה השתררה בכיתה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"מה עכשיו?\" לחש דראקו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא מעולם לא היה כה מבועת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"זה עדיין לא ודאי,\" אמר הארי פוטר. \"הניסוי שלי נכשל, זוכר? אני צריך שתתכנן בדיקה נוספת, דראקו.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אני, אני...\" אמר דראקו. קולו נשבר. \"אני לא יכול לעשות את זה הארי, זה יותר מדי בשבילי.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">המבט של הארי היה מלא ברגש עז. \"אתה כן יכול, מפני שאתה חייב. גם אני חשבתי על זה, אחרי ששמעתי על האיסור של מרלין. דראקו, האם יש דרך לבחון את הכוח של הקסם ישירות? איזושהי דרך שלא קשורה לדם קוסמים או ללחשים שאנחנו מטילים?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">מוחו של דראקו היה ריק.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"כל מה שמשפיע על קסם משפיע על קוסמים,\" אמר הארי. \"אבל אז אנחנו לא יכולים לדעת האם זה הקוסמים או הקסם. על מה משפיע הקסם שהוא </span><span class=\"c1\">לא</span><span class=\"c2\">&nbsp;קוסם?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"יצורים קסומים, כמובן,\" אמר דראקו בלי לחשוב.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי פוטר חייך לאיטו. \"דראקו, זה </span><span class=\"c1\">מבריק</span><span class=\"c2\">.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c1\">זו מסוג השאלות המפגרות שתשאל מלכתחילה רק אם גדלת אצל מוגלגים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז, כשהבין מה המשמעות של היחלשות של יצורים קסומים, הבחילה בבטנו של דראקו החריפה אף יותר. הם ידעו בוודאות שהקסם נמוג, והיה חלק בדראקו שהיה בטוח שזה בדיוק מה שהם יגלו. הוא לא רצה לראות את זה, לא רצה לדעת...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי פוטר כבר היה בחצי הדרך לדלת. \"</span><span class=\"c1\">קדימה</span><span class=\"c2\">, דראקו! יש דיוקן לא רחוק מכאן, נבקש ממנו להביא מישהו זקן ונברר מיד! בוא נזוז!\"</span></p><hr><p class=\"c4\" dir=\"rtl\"><span class=\"c2\"></span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה לא לקח זמן רב.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">היה זה דיוקן רחב, אבל נראה היה ששלושת האנשים שהיו בו הרגישו דחוסים למדי. היה שם גבר בגיל העמידה מהמאה השתיים־עשרה, לבוש ביריעות בד שחורות; הוא דיבר עם אישה צעירה ועצובה למראה מהמאה הארבע־עשרה, ששיערה עמד באופן תמידי, כאילו נטענה בלחש חשמל סטטי; והיא בתורה דיברה עם גבר זקן, מכובד ומקומט מהמאה השבע־עשרה, שענד עניבת פרפר זהובה; ואותו הם יכלו להבין.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הם שאלו על סוהרסנים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הם שאלו על עופות־חול.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הם שאלו על דרקונים וטרולים וגמדוני־בית.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי כיווץ את מצחו, ציין שיכול להיות שהיצורים שנזקקו לכמות הקסם הגדולה ביותר נכחדו לחלוטין, ושאל על היצורים הקסומים החזקים ביותר הידועים לקוסמים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">לא היו ברשימה דברים שלא הכירו, למעט זן של יצורי אופל שנקראו מצליפי מוח, שהוכחדו על ידי הרולד שיאה, לטענת הזקן המתרגם, והם כלל לא נשמעו מפחידים כמו סוהרסנים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה נראה שיצורים קסומים היו חזקים כיום ממש כמו בעבר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הבחילה בבטנו של דראקו החלה לחלוף, ועכשיו הוא הרגיש רק בלבול.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"הארי,\" אמר דראקו, תוך שהאיש הזקן מתרגם את רשימת אחד־עשר הכוחות של עינו של יצור בשם בוהה, \"מה זה אומר?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי הרים אצבע והאיש הזקן סיים את הרשימה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז הארי הודה לכל הדיוקנאות על עזרתם — דראקו, באופן די אוטומטי, עשה זאת גם הוא, ובאדיבות רבה יותר — והם חזרו לכיתה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי הוציא את הקלף המקורי עם ההשערות, והחל לרשום.</span></p><p class=\"c0 c22\" dir=\"rtl\"><span class=\"c19 c14\"></span></p><p class=\"c27\" dir=\"rtl\"><span class=\"c28\">תצפית</span><span class=\"c9\">:</span></p><p class=\"c27\" dir=\"rtl\"><span class=\"c9\">הקסם כיום אינו חזק כפי שהיה כשטירת הוֹגווֹרטְס הוקמה.</span></p><p class=\"c27\" dir=\"rtl\"><span class=\"c28\">השערות</span><span class=\"c9\">:</span></p><ol class=\"c7 lst-kix_list_13-0 start\" start=\"1\"><li class=\"c37\" dir=\"rtl\"><span class=\"c9\">הקסם עצמו נמוג.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">קוסמים מתבוללים עם מוגלגים וסקיבים.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">הידע הנדרש כדי להטיל לחשים חזקים הולך לאיבוד.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">קוסמים לא אוכלים את המזון הנכון כילדים, או שמשהו אחר מלבד דם גורם להם לגדול חלשים יותר.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">טכנולוגיית מוגלגים מפריעה לקסם. (כבר 800 שנה?)</span></li><li class=\"c24\" dir=\"rtl\"><span class=\"c9\">לקוסמים חזקים יש פחות ילדים. (דראקו = בן יחיד? לבדוק האם ל־3 קוסמים חזקים, קווירל / דמבלדור / אדון האופל היו ילדים.)</span></li></ol><p class=\"c27\" dir=\"rtl\"><span class=\"c28\">בדיקות</span><span class=\"c9\">:</span></p><ol class=\"c7 lst-kix_list_16-0 start\" start=\"1\"><li class=\"c37\" dir=\"rtl\"><span class=\"c9\">האם קיימים לחשים שאנחנו מכירים אבל לא יכולים להטיל (1 או 2) או שהלחשים האבודים כבר אינם ידועים (3)? תוצאה: לא חד משמעי עקב האיסור של מרלין. אין לחש ידוע שלא ניתן להטלה, אבל ייתכן שפשוט לא נותר תיעוד.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">האם בימי קדם, תלמידי שנה ראשונה הטילו את אותם לחשים, באותה עוצמה כמו היום? (ראיה חלשה לטובת 1 על פני 2, אבל גם דם יכול לאבד רק קוסמוּת חזקה.) תוצאה: אותה רמה של לחשי שנה ראשונה כמו היום.</span></li><li class=\"c36\" dir=\"rtl\"><span class=\"c9\">בדיקה נוספת שמבחינה בין 1 ו־2 שמשתמשת בידע מדעי על דם, אסביר אחר־כך. תוצאה: יש רק מקום אחד במתכון שהופך אותך לקוסם, או שיש לך שני ניירות שאומרים 'קסם' או שלא.</span></li><li class=\"c24\" dir=\"rtl\"><span class=\"c9\">האם יצורים קסומים איבדו מכוחם? מבדיל את 1 מ־(2 או 3). תוצאה: נראה כאילו יצורים קסומים חזקים כיום כמו שהיו בעבר.</span></li></ol><p class=\"c0 c22\" dir=\"rtl\"><span class=\"c19 c14\"></span></p><p class=\"c6\" dir=\"rtl\"><span class=\"c2\">\"א' נכשל,\" אמר הארי פוטר. \"ב' היא ראיה חלשה ל־1 על פני 2. ג' מפריכה את 2. ד' מפריכה את 1. 4 הייתה לא סבירה ו־ב' מצביעה כנגד 4 גם כן. 5 הייתה לא סבירה וד' מצביעה כנגדה. 6 הופרכה יחד עם 2. זה משאיר את 3. האיסור של מרלין או לא, לא מצאתי שום לחש ידוע שלא ניתן להטילו. אז כשאתה מחבר את הכל יחד, נראה כאילו הידע אובד.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">והמלכודת נסגרה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ברגע שהפאניקה חלפה, ברגע שדראקו הבין שהקסם </span><span class=\"c1\">לא</span><span class=\"c2\">&nbsp;נמוג מהעולם, לקח לו בדיוק חמש שניות כדי להבין.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו דחף את עצמו מהשולחן ונעמד בכזו מהירות שהכיסא החליק לאחור בקול חריקה והתהפך.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אז זה היה בסך הכל תכסיס מטופש.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי פוטר בהה בו לרגע, עדיין יושב. כשדיבר, קולו היה שקט. \"זה היה ניסוי הוגן, דראקו. אם הוא היה יוצא אחרת, הייתי מקבל את התוצאה. זה לא משהו שאני ארמה בו. אף פעם. לא הסתכלתי על הנתונים שלך לפני שניסחתי את התחזיות שלי. אמרתי לך מראש שהאיסור של מרלין הפך את הניסוי הראשון ללא־תקף —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"הו,\" אמר דראקו, הכעס מתחיל לגלוש לקולו, \"לא ידעת איך כל העסק ייצא בסוף?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"לא </span><span class=\"c1\">ידעתי</span><span class=\"c2\">&nbsp;שום דבר שלא ידעת אתה,\" אמר הארי, עדיין בשקט. \"אני מודה שחשדתי. הרמיוני גריינג'ר הייתה חזקה מדי, היא הייתה אמורה להיות קסומה בקושי, והיא לא. איך בת מוגלגים יכולה להיות הקוסמת הטובה בהוֹגווֹרטְס? והיא גם קיבלה את הציונים הכי גבוהים על החיבורים שלה, זה צירוף מקרים גדול מדי, שלילדה אחת תהיה את יכולת קסם החזקה ביותר</span><span class=\"c1\">&nbsp;וגם</span><span class=\"c2\">&nbsp;את היכולת האקדמית הטובה ביותר, אלא אם יש סיבה אחת לשני הדברים. קיומה של הרמיוני גריינג'ר הצביע על כך שיש רק דבר אחד שהופך אותך לקוסם, משהו שיש לך או שאין לך, וההבדלים בכוח נובעים ממה שאנחנו יודעים ומכמה שאנחנו מתאמנים. ולא היו שיעורים נפרדים לטהורי־דם ולבני־מוגלגים, וכולי. היו דרכים רבות מדי בהן העולם היה נראה אחרת, לו היית צודק. אבל דראקו, לא ראיתי שום דבר שאתה לא יכולת לראות. לא עשיתי שום בדיקות שלא סיפרתי לך עליהן. לא רימיתי, דראקו. רציתי שנגלה את התשובה ביחד. ומעולם לא חשבתי שהקסם עשוי לחלוף מהעולם עד שאמרת את זה. זה היה רעיון מפחיד גם בשבילי.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"שיהיה,\" אמר דראקו. הוא התאמץ מאוד לשלוט בקולו ולא להתחיל לצרוח על הארי. \"אתה טוען שאתה לא עומד לספר לאף אחד על זה.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"לא בלי לבדוק איתך קודם,\" אמר הארי. הוא הושיט את ידיו בהפצרה. \"דראקו, אני מנסה להיות הכי נחמד שאני יכול אבל </span><span class=\"c1\">העולם פשוט לא נראה ככה.</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"בסדר. אז אתה ואני גמרנו. אני אלך מפה ואשכח שזה בכלל קרה.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו הסתובב, מרגיש את תחושת הצריבה בגרונו, את תחושת הבגידה, ואז הוא הבין שהוא </span><span class=\"c1\">באמת</span><span class=\"c2\">&nbsp;חיבב את הארי פוטר, והמחשבה הזו לא האטה אותו לרגע כשצעד לכיוון דלת הכיתה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז הגיע קולו של הארי פוטר, חזק ומודאג יותר:</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"דראקו... אתה </span><span class=\"c1\">לא יכול</span><span class=\"c2\">&nbsp;לשכוח. אתה לא מבין? זה היה הקורבן שלך.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו עצר באמצע צעד והסתובב. \"על</span><span class=\"c1\">&nbsp;מה</span><span class=\"c2\">&nbsp;אתה מדבר?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל הוא כבר הרגיש את תחושת הקור המקפיא בעמוד השדרה שלו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא ידע אפילו לפני שהארי פוטר אמר זאת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"כדי להפוך למדען. הטלת ספק באחת מהאמונות שלך, לא סתם אמונה קטנה אלא משהו שהיה לו חשיבות גדולה עבורך. ביצעת ניסויים, אספת נתונים, והתוצאה הוכיחה שהאמונה שגויה. ראית את התוצאות והבנת את המשמעות שלהן.\" קולו של הארי פוטר רעד. \"תזכור, דראקו, אתה לא יכול להקריב אמונה </span><span class=\"c1\">אמיתית</span><span class=\"c2\">&nbsp;בדרך הזו, מפני שהניסויים יאשרו אותה במקום להפריך אותה. הקורבן שלך כדי להפוך למדען היה האמונה </span><span class=\"c1\">השגויה</span><span class=\"c2\">&nbsp;שלך שדם הקוסמים מתערבב ונחלש.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">זה לא נכון!</span><span class=\"c2\">\" אמר דראקו. \"לא הקרבתי את האמונה הזו. אני עדיין מאמין בזה!\" קולו התגבר, והקור התחזק.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי פוטר הניד בראשו. קולו נשמע בלחישה. \"דראקו... אני מצטער, דראקו, אתה </span><span class=\"c1\">לא</span><span class=\"c2\">&nbsp;מאמין בזה, לא עוד.\" קולו של הארי התחזק שוב. \"אני אוכיח לך את זה. דמיין שמישהו אומר לך שהוא מחזיק דרקון בבית שלו. אתה אומר לו שאתה רוצה לראות אותו. הוא אומר שזה דרקון בלתי נראה. אתה אומר בסדר, שתקשיב לו כשהוא זז. הוא אומר שהדרקון לא משמיע קול כלל. אתה אומר שתפזר קמח ותראה את קווי המתאר של הדרקון באוויר. הוא אומר שהדרקון חדיר לקמח. והדבר המסגיר הוא שהוא </span><span class=\"c1\">יודע מראש</span><span class=\"c2\">&nbsp;בדיוק אילו תוצאות ניסוי הוא יצטרך לתרץ. הוא </span><span class=\"c1\">יודע</span><span class=\"c2\">&nbsp;שהכל יראה כאילו אין דרקון, הוא יודע </span><span class=\"c1\">מראש</span><span class=\"c2\">&nbsp;בדיוק אילו תירוצים הוא יצטרך לספק. אז אולי הוא </span><span class=\"c1\">אומר</span><span class=\"c2\">&nbsp;שיש דרקון. אולי הוא </span><span class=\"c1\">מאמין</span><span class=\"c2\">&nbsp;שהוא מאמין שיש דרקון, זה נקרא אמונה־באמונה. אבל הוא לא באמת מאמין בזה. אתה יכול לטעות בקשר למה שאתה מאמין בו, רוב האנשים לא מבינים בכלל שיש הבדל בין להאמין במשהו ובין לחשוב שכדאי להאמין בו.\" הארי פוטר קם מהשולחן עכשיו, ועשה כמה צעדים לכיוון דראקו. \"דראקו, אתה לא מאמין עוד בטוהר־דם, אני אראה לך שלא. אם תורת טוהר־הדם נכונה, אז הרמיוני גריינג'ר לא הגיונית, נכון? אולי היא יתומה ממשפחת קוסמים שגודלה על ידי מוגלגים, כמוני? אני יכול ללכת לגריינג'ר ולבקש לראות תמונות של ההורים שלה, ולבדוק אם היא נראית כמוהם. האם תצפה שהיא תיראה שונה? שנלך לבצע את הבדיקה הזו?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"הם היו שמים אותה עם קרובי משפחה,\" אמר דראקו, קולו רועד. \"הם עדיין ייראו דומים.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אתה רואה. אתה יודע מראש את תוצאת הניסוי שתצטרך לתרץ. לו עדיין היית מאמין בטוהר־דם היית אומר, בטח, בוא נלך לבדוק, אני מתערב איתך שהיא לא תיראה כמו ההורים שלה, היא חזקה מדי מכדי להיות בת מוגלגים —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"הם </span><span class=\"c1\">היו</span><span class=\"c2\">&nbsp;שמים אותה עם קרובי משפחה!\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"מדענים יכולים לעשות בדיקות כדי לוודא שמישהו הוא צאצא של אביו. גריינג'ר בטח תעשה את זה אם אשלם למשפחה שלה מספיק. </span><span class=\"c1\">היא</span><span class=\"c2\">&nbsp;לא תפחד מהתוצאות. אז מה אתה מצפה שהבדיקה הזו תראה? תגיד לי לעשות אותה ונראה. אבל אתה כבר יודע מה הבדיקה תראה. אתה תמיד תדע. לעולם לא תהיה מסוגל לשכוח. יכול להיות </span><span class=\"c1\">שתרצה</span><span class=\"c2\">&nbsp;להאמין בטוהר־דם, אבל תמיד </span><span class=\"c1\">תצפה שיקרה</span><span class=\"c2\">&nbsp;בדיוק מה שיקרה לו היה רק דבר אחד שהופך אותך לקוסם. זה היה הקורבן שלך כדי להפוך למדען.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">נשימתו של דראקו הייתה מאומצת. \"אתה מבין </span><span class=\"c1\">מה עשית?</span><span class=\"c2\">\" דראקו זינק קדימה ותפס את הארי בצווארון גלימותיו. קולו עלה לצרחה, הוא נשמע חזק במידה בלתי־נסבלת בכיתה הסגורה והשקטה. \"</span><span class=\"c1\">אתה מבין מה עשית?</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">קולו של הארי רעד. \"הייתה לך אמונה. האמונה הייתה שגויה. עזרתי לך לראות את זה. </span><span class=\"c1\">מה שאמיתי הוא כבר האמת, להכיר בו לא ירע את המצב </span><span class=\"c2\">—\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">יד ימין של דראקו נקפצה לאגרוף והיא צנחה מטה והתפרצה למעלה, בלתי ניתנת לעצירה, וחבטה בלסתו של הארי פוטר, כה חזק שגופו התרסק לתוך שולחן ומשם לרצפה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אידיוט!</span><span class=\"c2\">\" צרח דראקו. \"</span><span class=\"c1\">אידיוט! אידיוט!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"דראקו,\" לחש הארי מהרצפה, \"דראקו, אני מצטער, לא חשבתי שזה יקרה לפני שיעברו לפחות כמה חודשים, לא ציפיתי שתתעורר כמדען כל־כך מהר, חשבתי שיהיה לי יותר זמן להכין אותך, ללמד אותך את הטכניקות שיקלו עליך להכיר בזה שאתה טועה —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"ומה עם אבא?\" שאל דראקו. קולו רעד בזעם. \"עמדת להכין גם </span><span class=\"c1\">אותו</span><span class=\"c2\">&nbsp;או שפשוט לא </span><span class=\"c1\">אכפת </span><span class=\"c2\">לך מה יקרה אחרי זה?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אתה לא יכול לספר </span><span class=\"c1\">לו!</span><span class=\"c2\">\" אמר הארי, קולו עולה בדאגה. \"הוא לא מדען! הבטחת, דראקו!\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">לרגע, המחשבה על כך שאביו לא יידע הייתה הקלה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז החל לעלות בו הכעס האמיתי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אז תכננת שאני אשקר לו ואומר לו שאני עדיין מאמין,\" אמר דראקו, קולו רועד. \"אני תמיד אצטרך לשקר לו, ועכשיו כשאגדל אני לא אוכל להיות אוכל־מוות, ואני אפילו לא אוכל לספר לו למה לא.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אם אביך באמת אוהב אותך,\" לחש הארי מהרצפה, \"הוא עדיין יאהב אותך, אפילו אם לא תהפוך לאוכל־מוות, ונשמע כאילו אביך </span><span class=\"c1\">באמת</span><span class=\"c2\">&nbsp;אוהב אותך —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"האבא החורג </span><span class=\"c1\">שלך</span><span class=\"c2\">&nbsp;הוא מדען,\" אמר דראקו. המילים יצאו מפיו, חותכות כמו סכינים. \"אם </span><span class=\"c1\">אתה </span><span class=\"c2\">לא היית הופך למדען, הוא עדיין היה אוהב אותך. אבל היית </span><span class=\"c1\">קצת פחות מיוחד</span><span class=\"c2\">&nbsp;בשבילו.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי התכווץ. הוא פתח את פיו, כמו בכדי לומר</span><span class=\"c1\">&nbsp;'אני מצטער'</span><span class=\"c2\">, ואז סגר את פיו, כאילו הסיק שמוטב אחרת, מה שהפך אותו למאוד חכם או מאוד בר־מזל, כי דראקו היה עלול לנסות להרוג אותו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"היית צריך להזהיר אותי,\" אמר דראקו. קולו עלה. \"</span><span class=\"c1\">היית צריך להזהיר אותי!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אני... אני ניסיתי... כל פעם שסיפרתי לך על הכוח סיפרתי לך על המחיר. אמרתי שאתה חייב להודות שאתה טועה. אמרתי שזו תהיה הדרך הקשה ביותר עבורך. שזה היה הקורבן שצריך להקריב כל מי שרוצה להפוך למדען. אמרתי, מה אם הניסוי יאמר משהו אחד והמשפחה והחברים שלך יאמרו משהו אחר —\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אתה קורא לזה אזהרה?</span><span class=\"c2\">\" דראקו צרח עכשיו. \"</span><span class=\"c1\">אתה קורא לזה אזהרה? כשאתה מבצע טקס שדורש להקריב משהו לנצח?</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אני... אני...\" הילד שעל הרצפה בלע את רוקו. \"אני מניח שאולי זה לא היה ברור. אני מצטער. אבל מה שיש ביכולתה של האמת להשמיד, ראוי שיושמד.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">להרביץ לו כבר לא היה מספיק.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"אתה טועה בקשר לדבר אחד,\" אמר דראקו, קולו קטלני. \"גריינג'ר היא לא התלמידה הכי חזקה בהוֹגווֹרטְס. היא פשוט מקבלת את הציונים הכי טובים. אתה עומד לגלות את ההבדל.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הלם פתאומי הופיע על פניו של הארי, והוא ניסה להתגלגל ולקום במהירות —</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה כבר היה מאוחר מדי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אֶקְסְפֶּלִיאַרְמוּס!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">שרביטו של הארי עף לצדו השני של החדר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">גוֹם גַ'בָּאר!\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ברק שחור כדיו הכה ביד שמאל של הארי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"זה לחש עינויים,\" אמר דראקו. \"משתמשים בו בשביל להוציא מידע מאנשים. אני פשוט הולך להשאיר אותו פועל ולנעול את הדלת מאחורי. אולי אני אטיל את לחש הנעילה כך שיתפוגג אחרי כמה שעות. אולי הוא לא יתפוגג עד שתמות פה. תהנה.\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו נע לאחור באלגנטיות, שרביטו עדיין מכוון אל הארי. ידו צללה והרימה את תיקו, שרביטו נשאר מכוון ביציבות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הכאב כבר החל להופיע על פניו של הארי פוטר, כשדיבר. \"המאלפויים נמצאים מעל החוק לבקרת קוסמות בקרב קטינים, אני מבין? זה לא בגלל שהדם שלך חזק יותר. זה בגלל שכבר התאמנת. בהתחלה היית חלש כמו כולנו. האם התחזית שלי שגויה?\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ידו של דראקו הלבינה על שרביטו, אבל הוא לא הסיט אותו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"רק שתדע,\" אמר הארי דרך שיניים חשוקות, \"אם היית אומר לי שאני טועה הייתי מקשיב. </span><span class=\"c1\">אני</span><span class=\"c2\">&nbsp;לעולם לא אענה </span><span class=\"c1\">אותך</span><span class=\"c2\">&nbsp;כשתראה לי שאני טועה. וזה ללא ספק יקרה, יום אחד. התעוררת כמדען עכשיו, וגם אם לעולם לא תלמד להשתמש בכוח שלך, אתה תמיד,\" השתנק הארי, \"תחפש, דרכים, לבחון, את, האמונות, שלך&nbsp;—\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">תנועתו של דראקו לאחור הייתה פחות אלגנטית כעת, מעט מהירה יותר, והוא נאלץ להתאמץ כדי לשמור את שרביטו מכוון אל הארי כששלח את ידו לאחור כדי לפתוח את הדלת ויצא מהכיתה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז דראקו סגר את הדלת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא הטיל את לחש הנעילה החזק ביותר שהכיר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו המתין עד ששמע את הצרחה הראשונה של הארי לפני שהטיל </span><span class=\"c1\">קְווָיֵטוּס</span><span class=\"c2\">.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז הוא הלך.</span></p><hr><p class=\"c4\" dir=\"rtl\"><span class=\"c2\"></span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אאאההה! פִינִיטֶה אִינְקַנטָאטֶם! אאאאההה!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ידו השמאלית של הארי הוטבלה בסיר של שמן רותח. הוא השקיע את כל מה שהיה לו ב</span><span class=\"c1\">פִינִיטֶה אִינְקַנטָאטֶם</span><span class=\"c2\">&nbsp;וזה עדיין לא עבד.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">קללות מסוימות דרשו לחשי נגד ספציפיים לביטולן, או אולי דראקו היה פשוט כל־כך יותר חזק ממנו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אאאאאההה!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ידו של הארי ממש כאבה עכשיו, וזה הפריע לנסיונות שלו לחשוב בצורה יצירתית.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל כמה צרחות לאחר מכן, הארי הבין מה עליו לעשות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">למרבה הצער, הוא נשא את הנרתיק שלו בצד הלא נכון של הגוף, והוא נדרש למידה מסוימת של התפתלויות כדי להגיע אליו, במיוחד כשידו השנייה מתנופפת לכל עבר בנסיון רפלקסיבי ובלתי נשלט לסלק את מקור הכאב. כשהצליח לעשות זאת בסופו של דבר, ידו השנייה הצליחה לזרוק שוב את שרביטו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"ערכת </span><span class=\"c1\">אאאה</span><span class=\"c2\">&nbsp;מרפא! ערכת מרפא!\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">על הרצפה, היה קשה לראות באור הירוק העמום.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי לא הצליח לעמוד. הוא לא הצליח לזחול. הוא התגלגל על הרצפה עד המקום שבו חשב שהיה שרביטו, והוא לא היה שם. הוא הצליח להרים את עצמו על יד אחת כדי לאתר את השרביט, ואז התגלגל לשם, לקח את השרביט, והתגלגל חזרה לערכת המרפא הפתוחה. הדבר כלל גם לא מעט צרחות וקצת הקאות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">נדרשו להארי שמונה ניסיונות לפני שהצליח להטיל </span><span class=\"c1\">לוּמוֹס</span><span class=\"c2\">.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז, ובכן, הערכה לא תוכננה להיפתח ביד אחת, מפני שקוסמים היו מטומטמים. הארי נאלץ להשתמש בשיניו ולכן לקח זמן מה לפני שהארי הצליח לעטוף את ידו השמאלית בבַדילְחוּש.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">כשכל תחושה מידו השמאלית נעלמה, הארי נתן לתודעתו להתפרק, שכב חסר תנועה על הרצפה לזמן מה, ובכה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c1\">ובכן</span><span class=\"c2\">, חשב לעצמו הארי, כשתודעתו התאוששה מספיק כדי לחזור לחשוב במילים. </span><span class=\"c1\">האם זה היה שווה את זה?</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">היד המתפקדת של הארי נשלחה לאיטה מעלה אל השולחן.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי משך את עצמו על רגליו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">לקח נשימה עמוקה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">נשף.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">חייך.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה לא היה חיוך מרשים במיוחד, אבל בכל זאת חיוך.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c1\">תודה לך, פרופסור קווירל, לא הייתי מצליח להפסיד בלעדיך.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא טרם גאל את דראקו, אפילו לא קרוב לכך. בניגוד למה שדראקו עצמו אולי חושב כרגע, הוא עדיין בנו של אוכל־מוות, במלוא מובן המילה. הוא עדיין ילד שגדל במחשבה ש\"אונס\" זה משהו שהילדים הגדולים והמגניבים עשו. אבל זו הייתה חתיכת התחלה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי לא יכל לטעון שהכל הלך כמתוכנן. הכל הלך כמאולתר במקום. </span><span class=\"c1\">התכנית</span><span class=\"c2\">&nbsp;הייתה שהדבר לא יקרה לפני דצמבר לכל המוקדם, אחרי שהארי ילמד את דראקו לא להתכחש לראיות כשנחשף אליהן.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל הוא ראה את מבט הפחד על פניו של דראקו, הבין שדראקו שוקל השערה חלופית ברצינות </span><span class=\"c1\">כבר עכשיו</span><span class=\"c2\">, וניצל את ההזדמנות. למקרה אחד של סקרנות אמיתית היה את אותו הכוח לגאול אדם כמו שהיה לאהבת אמת בסרטים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">במבט לאחור, הארי הקציב לעצמו שעות בודדות לבצע את התגלית החשובה ביותר בהיסטוריה של הקסם, ומספר חודשים כדי לפרוץ את המגננות המנטליות של ילד בן אחת־עשרה. הדבר עלול להעיד על כך שלהארי יש סוג כלשהו של פער קוגניטיבי משמעותי בהערכת זמנים של משימות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">האם הארי ילך לגיהנום של המדע על מה שעשה? הוא לא היה בטוח. הוא תמרן את דראקו כך שימשיך לחשוב על האפשרות שקסם נמוג מהעולם, ווידא שהוא יבצע קודם כל את החלק בניסוי שנראה היה שיצביע לכיוון. הוא חיכה עד שהסביר על גנטיקה כדי לדחוף את דראקו לחשוב על יצורי פלא (אם כי הוא חשב על חפצים קסומים עתיקים כדוגמת מצנפת המיון, שאיש לא היה מסוגל לשכפל, אבל המשיכו לתפקד). אבל הארי באמת לא הפריז בחשיבותה של אף ראיה, לא עיוות את המשמעות של אף תוצאה. כשהאיסור של מרלין הפך את הבדיקה שהייתה אמורה להיות מכרעת לחסרת תוקף, הוא אמר זאת לדראקו מיד.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז בא החלק </span><span class=\"c1\">שאחרי</span><span class=\"c2\">&nbsp;זה...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל הוא לא באמת </span><span class=\"c1\">שיקר</span><span class=\"c2\">&nbsp;לדראקו. דראקו האמין בזה, </span><span class=\"c1\">וזה יהפוך את זה לאמיתי</span><span class=\"c2\">.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הסוף לא היה כיפי, יש להודות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי הסתובב ודידה לעבר הדלת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הגיע הזמן לבחון את לחש הנעילה של דראקו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הצעד הראשון היה פשוט לנסות לסובב את הידית. יכול להיות שדראקו בילף.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו לא בילף.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">פִינִיטֶה אִינְקַנטָאטֶם</span><span class=\"c2\">.\" קולו של הארי היה ניחר למדי, והוא היה מסוגל לחוש שהלחש לא פעל.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אז הארי ניסה שוב, והפעם זה הרגיש נכון. אבל סיבוב נוסף של הידית הוכיח שזה לא עבד. לא מפתיע.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הגיע הזמן להוציא את התותחים הכבדים. הלחש הזה היה אחד מהחזקים ביותר שלמד עד כה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">\"</span><span class=\"c1\">אַלוֹהוֹמוֹרָה!</span><span class=\"c2\">\"</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי התנדנד קלות אחרי שאמר זאת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ודלת הכיתה עדיין לא נפתחה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה הדהים את הארי. הארי לא תיכנן להתקרב למסדרון האסור של דמבלדור, כמובן. אבל לחש שפותח מנעולים קסומים הוא לחש שטוב לדעת בכל מקרה, אז הארי למד אותו. האם המסדרון האסור של דמבלדור היה אמור לפתות אנשים כה טיפשים שלא היו שמים לב לעובדה שהאבטחה הייתה פחות טובה ממה שדראקו מאלפוי היה מסוגל להטיל?</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הפחד החל להתגנב חזרה לתודעתו של הארי. ההוראות בערכת המרפא אמרו שניתן להשתמש בבדילחוש בצורה בטוחה למשך לא יותר משלושים דקות. לאחר מכן הוא יסיר עצמו באופן אוטומטי, ולא יהיה ניתן להשתמש בו שוב במשך 24 שעות. השעה כעת הייתה 6:51 בערב. הוא התעטף בבדילחוש לפני חמש דקות בערך.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אז הארי לקח צעד לאחור ובחן את הדלת. היא הייתה עשויה מלוח כבד של עץ אלון כהה, שעליו הורכבה ידית פליז.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי לא הכיר לחשים של פיצוץ או חיתוך או ריסוק, ולשנות־צורה של משהו לחומר נפץ היה מפר את החוק נגד שינוי צורה לדברים שנשרפים. חומצה היא נוזל שייצור אדים...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל זה לא היה מכשול בשביל מישהו שחושב</span><span class=\"c1\">&nbsp;ביצירתיות</span><span class=\"c2\">.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי הניח את שרביטו על אחד מצירי הפליז של הדלת, והתרכז בצורה של כותנה כהפשטה מנותקת מכל כותנה חומרית, וגם בפליז טהור במנותק מהתבנית שהפכה אותו לציר, וצירף את שני הרעיונות יחד, כופה צורה על החומר. אחרי שעה של תרגול שינוי צורה בכל יום במשך חודש, הארי היה מסוגל לשנות־צורה של מטרה בנפח של חמישה סנטימטרים מעוקבים בתוך מעט פחות מדקה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אחרי שתי דקות הציר נשאר ללא שינוי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">מי שתכנן את לחש הנעילה של דראקו חשב גם על זה, כנראה. או שהדלת הייתה חלק מהוֹגווֹרטְס והטירה הייתה חסינה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">מבט חטוף הראה לו שהקירות עשויים אבן מוצקה. כך גם הרצפה. כך גם התקרה. אי אפשר לשנות־צורה למשהו שהוא חלק משלם; הארי היה צריך לנסות לשנות־צורה של כל הקיר, מה שהיה לוקח שעות ואולי ימים של מאמץ רצוף, אם בכלל היה מצליח לעשות זאת, ואם הקיר לא היה חלק רציף משאר הטירה...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">מחולל־הזמן של הארי לא ייפתח עד 9 בערב. לאחר מכן הוא יוכל לחזור בזמן לשעה 6 בערב, לפני שהדלת ננעלה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">כמה זמן יימשך לחש העינוי?</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי בלע את רוקו בחוזקה. דמעות החלו נקוות שוב בעיניו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">המוח היצירתי להפליא שלו הציע הרגע את ההצעה הגאונית שהארי יגדע את ידו באמצעות המסור שבארגז הכלים בנרתיקו, מה שיכאב, כמובן, אבל אולי יכאב הרבה פחות מלחש העינוי של דראקו, מכיוון שהעצבים ייקטעו; והיו לו חסמי־עורקים בערכת המרפא.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">וזה היה רעיון מטופש להחריד שהארי יתחרט עליו למשך שארית חייו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">אבל הארי לא ידע אם הוא מסוגל להחזיק מעמד בשעתיים של עינויים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא רצה </span><span class=\"c1\">לצאת</span><span class=\"c2\">&nbsp;מהכיתה הזו, הוא רצה לצאת מהכיתה הזו </span><span class=\"c1\">עכשיו</span><span class=\"c2\">, הוא לא רצה לחכות כאן בצרחות במשך שעתיים עד שיוכל להשתמש במחולל־הזמן, הוא צריך </span><span class=\"c1\">לצאת</span><span class=\"c2\">&nbsp;ולמצוא מישהו שיסיר את לחש העינוי מהיד שלו...</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c1\">תחשוב!</span><span class=\"c2\">&nbsp;צרח הארי על המוח שלו. </span><span class=\"c1\">תחשוב! תחשוב!</span></p><hr><p class=\"c4\" dir=\"rtl\"><span class=\"c2\"></span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">המעונות של סְלִיתְ'רִין היו ריקים כמעט לגמרי. אנשים היו בארוחת הערב. דראקו עצמו לא הרגיש רעב במיוחד, מסיבה כלשהי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו סגר את הדלת לחדרו הפרטי, הטיל עליה לחש נעילה ולחש השתקה, התיישב על מיטתו, והחל לבכות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה לא הוגן.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זה לא הוגן.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זו הייתה הפעם הראשונה שדראקו באמת </span><span class=\"c1\">הפסיד</span><span class=\"c2\">. אבא הזהיר אותו שהפסד אמיתי כואב כשהוא קורה בפעם הראשונה. אבל הוא הפסיד </span><span class=\"c1\">כל־כך הרבה</span><span class=\"c2\">, זה לא הוגן, זה לא הוגן שהוא יפסיד </span><span class=\"c1\">הכול</span><span class=\"c2\">&nbsp;בפעם הראשונה שהוא מפסיד.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">איפשהו בצינוקים, ילד שדראקו באמת חיבב צרח בכאב. דראקו מעולם לא פגע לפני כן במישהו שהוא חיבב. להעניש אנשים שהעונש הגיע להם היה אמור להיות כיף, אבל מה שעשה פשוט גרם לו לתחושת בחילה. אבא לא הזהיר אותו לגבי זה, ודראקו תהה האם זה שיעור שכולם צריכים ללמוד כשהם מתבגרים, או שמא הוא פשוט חלש.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו היה מעדיף שזו הייתה פנסי שצורחת. זה היה מרגיש הרבה יותר טוב.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">והחלק הגרוע ביותר היה, שאולי זו הייתה טעות לפגוע בהארי פוטר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">מי עוד היה שם בשביל דראקו כעת? דמבלדור? אחרי מה שעשה? דראקו היה מעדיף שישרפו אותו חי.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו יצטרך לחזור להארי פוטר מכיוון שאין לו אל מי ללכת. ואם הארי פוטר יגיד שהוא לא רוצה בו, אזי דראקו יהיה שום דבר, רק ילד קטן ועלוב שלעולם לא יוכל להיות אוכל־מוות, לעולם לא יוכל להצטרף לפלג של דמבלדור ולעולם לא יוכל ללמוד מדע.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">המלכודת הונחה בצורה מושלמת והופעלה בצורה מושלמת. אבא הזהיר את דראקו שוב ושוב שאת מה שהקרבת בטקסים אפלים, לא ניתן לקבל בחזרה. אבל אבא לא ידע שהמוגלגים הארורים המציאו טקסים שלא דרשו שרביטים, טקסים שניתן לרמות אותך כך שתשתתף בהם ללא ידיעתך, וזה היה רק אחד מהסודות הנוראים שמדענים ידעו, והארי פוטר הביא עימו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו התחיל לבכות שוב, חזק יותר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא לא רצה בזה, הוא </span><span class=\"c1\">לא רצה בזה</span><span class=\"c2\">&nbsp;אבל לא הייתה דרך חזרה. זה היה מאוחר מדי. הוא כבר היה מדען.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">דראקו ידע שהוא צריך לחזור ולשחרר את הארי פוטר ולהתנצל. זה יהיה הדבר החכם לעשות.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">במקום זאת הוא נשאר במיטתו והתייפח.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הוא כבר הכאיב להארי פוטר. ייתכן שזו תהיה הפעם היחידה שדראקו יזכה להכאיב לו, והוא יהיה חייב להיאחז בזיכרון האחד הזה למשך שארית חייו.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">שימשיך לצרוח.</span></p><hr><p class=\"c4\" dir=\"rtl\"><span class=\"c2\"></span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי שמט את שארית המסור לרצפה. צירי הפליז הוכיחו את עצמם כחסינים, הם אפילו לא נשרטו, והארי התחיל לחשוב שאפילו מעשה ייאוש של שינוי צורה של משהו לחומצה או לחומר נפץ לא היה מצליח לפתוח את הדלת. לפחות הדבר החיובי היה שהניסיון השמיד את המסור.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">השעון שלו הורה על 7:02. נותרה פחות מרבע שעה, והארי ניסה להיזכר אם יש עוד דברים חדים בנרתיק שלו שהוא צריך להרוס, והרגיש עוד דמעות נקוות. לו יוכל, כאשר מחולל־הזמן ייפתח, לחזור אחורה </span><span class=\"c1\">ולמנוע</span><span class=\"c2\">&nbsp;—</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז הארי הבין שהוא מתנהג בצורה </span><span class=\"c1\">מגוחכת</span><span class=\"c2\">.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">זו לא הייתה הפעם הראשונה שבה הוא ננעל בחדר.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">פרופסור מֶקגונַגל כבר אמרה לו מהי הדרך הנכונה לעשות זאת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">...היא אמרה לו לא להשתמש במחולל־הזמן לדברים כאלה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">האם פרופסור מֶקגונַגל תבין שהאירוע הזה </span><span class=\"c1\">כן</span><span class=\"c2\">&nbsp;מצריך חריגה מיוחדת? או שהיא פשוט תיקח את מחולל־הזמן?</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">הארי אסף את כל הדברים שלו, את כל הראיות, לתוך הנרתיק. לחש </span><span class=\"c1\">התקרצף </span><span class=\"c2\">טיפל בקיא שעל הרצפה, אם כי לא בגלימותיו ספוגות הזיעה. הוא השאיר את השולחנות ההפוכים הפוכים, הם לא היו שווים את המאמץ של לסדר אותם עם יד אחת.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">כשסיים הוא הציץ בשעונו. 7:04.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">ואז הארי המתין. שניות עברו, מרגישות כמו שנים.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">בשעה 7:07, הדלת נפתחה.</span></p><p class=\"c0\" dir=\"rtl\"><span class=\"c2\">פניו עטורות הזקן של פרופסור פליטיק נראו מודאגות למדי. \"אתה בסדר, הארי?\" אמר קולו הצפצפני של של ראש בית רֵייבֶנְקְלוֹ. \"קיבלתי פתק שאומר שננעלת פה —\"</span></p><hr style=\"page-break-before:always;display:none;\"><p class=\"c31 c22\"><span class=\"c30 c14\"></span></p><h2 class=\"c3\" dir=\"rtl\" id=\"h.tyjcwt\"><span class=\"c12\"></span></h2></div>",
                    HtmlCompat.FROM_HTML_MODE_COMPACT
                ) }
            )

